/* PRELOAD IMAGES */
/*
USAGE:
Plugin Version:
$(['img1.jpg','img2.jpg','img3.jpg']).preload();

Function Version:
preload([
    'img/imageName.jpg',
    'img/anotherOne.jpg',
    'img/blahblahblah.jpg'
]);
*/
$.fn.preload = function() {
	this.each(function(){
		$('<img />').attr('src',this).appendTo('body').hide();
	});
};;
function preload(arrayOfImages) {
	$(arrayOfImages).each(function () {
	 $('<img />').attr('src',this).appendTo('body').hide();
	});
}

